var pomelo = require('pomelo');
var utils = require('../util/utils');
var async = require('async');

var roomDao = module.exports;

/**
 * Get account data by account.
 * @param {String} account
 * @param {String} passwd
 * @param {function} cb
 */
roomDao.create = function (uid, roomname, cb) {
	var sql = 'insert into game_classroom_info (uid,roomname,createTime) values(?,?,?)';
	var time = Math.floor(new Date().getTime()/1000);
	var args = [uid, roomname, time];
	pomelo.app.get('dbclient').query(sql,args,function(err, res) {
		if(err){
			utils.invokeCallback(cb, err, null);
		}else{
			utils.invokeCallback(cb, err, {"id":res.insertId});
		}
	});
};


roomDao.join = function (uid, roomname, cb) {
	var connection = pomelo.app.get('dbclient');
	var list = {},sql,args,data;
	async.series([
		function(callback){
			sql = 'select * from game_classroom_user where uid = ? and roomname = ?';
			args = [uid,roomname];
			connection.queryOne(sql,args,function(err, res) {
				if (!!res){
					data = res;
				}
				callback(err);
			});
		},function(callback){
			if(data) callback(null);
			else{
				sql = 'insert into game_classroom_user (uid,roomname) values(?,?)';
				args = [uid, roomname];
				connection.query(sql,args,function(err, res) {
					callback(err);
				});
			}
		},function(callback){
			sql = 'select * from game_classroom_user where roomname = ?';
			args = [roomname];
			connection.query(sql,args,function(err, res) {
				list = res;
				callback(err);
			});
		}],function(err,results) {
			utils.invokeCallback(cb, err, {"list":list,"roomname":roomname});
	});
};


roomDao.exit = function (uid,roomname, cb) {
	var sql = 'delete from game_classroom_user where uid = ?';
	var args = [uid];
	pomelo.app.get('dbclient').query(sql,args,function(err, res) {
		if(err){
			utils.invokeCallback(cb, err, null);
		}else{
			utils.invokeCallback(cb, err, {"id":uid,"roomname":roomname});
		}
	});
};
